!INC P1Core.P1Common

function OnProjectBrowserScript()
{
	// Get the type of element selected in the Project Browser
	var treeSelectedType = Repository.GetTreeSelectedItemType();

	// Handling Code: Uncomment any types you wish this script to support
	// NOTE: You can toggle comments on multiple lines that are currently
	// selected with [CTRL]+[SHIFT]+[C].
	switch ( treeSelectedType )
	{
		case otElement :
		{
			var theElement as EA.Element;
			theElement = Repository.GetTreeSelectedObject();

			//check whether its a system object
			if(isSystem(theElement))
			{
				//add the child subsystem element in the right place
				var SSName = Session.Input("Please Enter SS Name");
				addSubSystem(SSName,theElement);	
			}

			break;
		}
		default:
		{
			// Error message
			Session.Prompt( "System object must be selected", promptOK );
		}
	}
}

