!INC P1Core.P1Solution

/*
 * This code has been included from the default Project Browser template.
 * If you wish to modify this template, it is located in the Config\Script Templates
 * directory of your EA install path.   
 * 
 * Script Name:
 * Author:
 * Purpose:
 * Date:
 */

/*
 * Project Browser Script main function
 */
function OnProjectBrowserScript()
{
	// get selected element type
	var treeSelectedType = Repository.GetTreeSelectedItemType();

	switch ( treeSelectedType )
	{
		case otElement :
		{
			// get selected element
			var theElement as EA.Element;
			theElement = Repository.GetTreeSelectedObject();

			// locate the Architecture Views package
			// FOR TESTING: navigate to the Architecture Views package
			var thePackage as EA.Package;
			thePackage = Repository.GetTreeSelectedPackage();
			while (thePackage.Name != 'Diagram Update Test') 
			{
				thePackage = Repository.GetPackageByID(thePackage.Element.PackageID);
			}
			var arcViewPkg as EA.Package;
			arcViewPkg = thePackage.Packages.GetByName('Architecture Views')
			// FOR ACTUAL USE:
			// arcViewPkg = getPackages().arcViewPkg <--- USE THIS

			// find correct decomp diagram
			var theDiagram as EA.Diagram;
			var packageDiagrams as EA.Collection;
			switch (theElement.Stereotype)
			{	
				case 'Subsystem':
				{
					// find parent system
					var parentSys as EA.Element;
					parentSys = getParentSystem(theElement);
					// navigate to Application Architecture package for subsystem
					thePackage = arcViewPkg.Packages.GetByName('System');
					thePackage = thePackage.Packages.GetByName(parentSys.Name);
					thePackage = thePackage.Packages.GetByName('SubSystem');
					thePackage = thePackage.Packages.GetByName(theElement.Name);
					thePackage = thePackage.Packages.GetByName('Application Architecture');
					break;
				}	
				case 'System':
				{
					// navigate to Application Architecture package for system
					thePackage = arcViewPkg.Packages.GetByName('System');
					thePackage = thePackage.Packages.GetByName(theElement.Name);
					thePackage = thePackage.Packages.GetByName('Application Architecture');
					break;
				}
				default:
				{
					Session.Output('This function requires a system or subsystem as input.');
				}
			}

			// get decomposition diagram (look for 'Decomposition' diagram type)
			packageDiagrams = thePackage.Diagrams;
			for (var i = 0; i < packageDiagrams.Count; i++) {
				theDiagram = packageDiagrams.GetAt(i);
				if (theDiagram.Type == 'Decomposition') {
					break;
				}
			}

			updateDiagram(theElement,theDiagram);
			Session.Prompt(theElement.Name+' Decomposition Diagram Updated', promptOK );

			break;
		}
		default:
		{
			// Error message
			Session.Prompt( "This script does not support items of this type.", promptOK );
		}
	}
}

OnProjectBrowserScript();
